package corejava;

public class charator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch;
		ch = 'X';

		System.out.println("ch contains " + ch);
		ch++; // increment ch
		System.out.println("ch is now " + ch); 
		ch = 90; // give ch the value Z 
		System.out.println("ch is now " + ch);
		

	}

}
